import React from 'react'
import { useDispatch, useSelector } from 'react-redux';

const Productlist = ({ products }) => {

  const cartItmes = useSelector(state => state.cart.items);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(
      {
        type: 'CHANGE_ORDER_CART',
        payload: cartItmes,
      }
    )
  }, [cartItmes])

  const addToCart = (product) => {
    dispatch({
      type: 'ADD_TO_CART',
      payload: product,
    })
  }

  return (

    <div>
      <div className="container mb-5">
        <div id="products" className="row">
          {products.map((product) => <div key={product.id} className="col-xl-3 col-lg-4 col-md-6 position-relative">
            <div className="card product-item">
              <i className="bi bi-heart-fill position-absolute liked"></i>
              <i className="bi bi-heart position-absolute like"></i>
              <img src={`images/${product.image}.jpg`} className="card-img-top" alt="..." data-bs-toggle="tooltip" data-bs-placement="top" title="Click to See Product Details" />
              <div className="card-body">
                <h6 className="card-subtitle mb-2 text-muted  fw-light">Headphones</h6>
                <h5 className="card-title">{product.name}</h5>
                <p className="card-text price"> $100.75 <span className="float-end rating-stars" >
                  <i className="bi bi-star-fill"></i>
                  <i className="bi bi-star-fill"></i>
                  <i className="bi bi-star-fill"></i>
                </span> </p>
                <div className="text-center">
                  <a className="btn btn-dark w-100" onClick={(e) => addToCart(product)} role="button" > Add To Cart</a>
                </div>
              </div>
            </div>
          </div>)}
        </div>
      </div>
    </div>
  )
}

export default Productlist
